import Data from '../../data/db.json'
import { useParams } from 'react-router-dom' 

export default function TourDetails() {
  const id = useParams()
  let tourDiv;
  Data.forEach((e) => e.id == id ? 
  tourDiv = 
  <div key={e.id}>
    <img src={e.image}/>
    <h3>{e.name}</h3>
    <p>{e.info}</p>
    <p>{e.price}</p>
  </div>
  : ``
  )
  return (
    <>
    {tourDiv}
    </>
  )
}
